(1, 'XII', '', 'DUODEKA', 'https://duodeka.nl', NULL, '', NULL, NULL),
(2, '', '', '', 'https://www.linkedin.com/in/klavrijssen', NULL, '', NULL, NULL),
(3, '', '', '', 'https://www.linkedin.com/in/rik-van-de-looi', NULL, '', NULL, NULL),
